-- server/data/indian_credit_cards.sql

-- Drop the table if it already exists to ensure a clean start
DROP TABLE IF EXISTS indian_credit_cards;

-- Create the indian_credit_cards table
CREATE TABLE indian_credit_cards (
    card_id INTEGER PRIMARY KEY AUTOINCREMENT, -- AUTOINCREMENT for easy primary key management
    name TEXT NOT NULL,
    issuer TEXT NOT NULL,
    joining_fee REAL,
    annual_fee REAL,
    reward_type TEXT,
    reward_rate TEXT,
    eligibility_criteria TEXT,
    special_perks TEXT,
    apply_link TEXT,
    image_url TEXT -- Added column for card image URL
);

